import React from "react";
import {addFavourite,removeFavourite} from '../actions';

class MovieCard extends React.Component {
    handleFavouriteClick = ()=>{
        const {movie} = this.props;
        this.props.dispatch(addFavourite(movie));
      
    }
    handleUnFavouriteClick = ()=>{
        const {movie} = this.props;
        this.props.dispatch(removeFavourite(movie));
    }
    render(){
        const {movie, isFavourite} = this.props
        // console.log(movie)
        return (
            <div className="card col-sm-6 col-md-4">
                {/* <div className="row"> */}
                    <div className="col-sm-12">
                        <img className="img-fluid w-100 " src={movie.photo['full']}/>
                    </div>
                    <div className="col-sm-12">
                        <div className="card-body">
                            <h3 className="card-title">{movie.name} </h3>
                            {/* <small className="text-muted">WEig{movie.weight}</small> */}
                            <h5 className="card-title">Favourite Food</h5>
                            <p className="card-text">{movie.favoriteFood}</p>
                            <h5 className="card-title">Weight</h5>
                            <p className="card-text">{movie.weight}</p>
                            {/* <h5 className="card-title">Writers</h5>
                            <p className="card-text">{movie.Writer}</p>
                            <h5 className="card-title">Director</h5>
                            <p className="card-text">{movie.Director}</p> */}
                            <div className="footer">
                                {/* <div className="rating">IMDB : {movie.imdbRating}</div> */}
                                {
                                    isFavourite
                                    ? <button className="btn btn-danger fw-bolder" onClick={this.handleUnFavouriteClick}>Retrieve</button>
                                    : <button className="btn btn-secondary fw-bolder" onClick={this.handleFavouriteClick}>Adopt</button>
                                }
                                
                            </div>
                        </div>
                    </div>
                {/* </div> */}
            </div>
        )
    }  
}
  
  export default MovieCard;
